import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Dimensions } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Import Ionicons from Expo

export default function Navbar({ onCategorySelect }) {
  const screenWidth = Dimensions.get('window').width;
  const isMobile = screenWidth <= 600; // Define a breakpoint for mobile devices

  // Render the hamburger icon for mobile devices
  if (isMobile) {
    return (
      <View style={styles.navbar}>
        <TouchableOpacity onPress={() => { /* Toggle a mobile menu or navigation drawer */ }}>
          <Ionicons name="ios-menu" size={30} color="white" />
        </TouchableOpacity>
        <View style={styles.logo}>
          <Text style={styles.logoText}>KMART</Text>
        </View>
      </View>
    );
  }

  // Render the full version for laptops and wider screens
  return (
    <View style={styles.navbar}>
      <TouchableOpacity onPress={() => onCategorySelect('Home')}>
        <Text style={styles.navItem}>Home</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Men')}>
        <Text style={styles.navItem}>Men</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Women')}>
        <Text style={styles.navItem}>Women</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Electronics')}>
        <Text style={styles.navItem}>Electronics</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Kids')}>
        <Text style={styles.navItem}>Kids</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Home-Decor')}>
        <Text style={styles.navItem}>Home-Decor</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Profile')}>
        <Text style={styles.navItem}>Profile</Text>
      </TouchableOpacity>
      <View style={styles.logo}>
        <Text style={styles.logoText}>KMART</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  navbar: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: 'darkslategray',
    paddingHorizontal: 10,
    height: 60,
    width: '100%',
  },
  
  navItem: {
    color: 'white',
    marginHorizontal: 10,
    fontSize: 18,
  },
  
  logo: {
    paddingRight: 20,
    paddingLeft: 20,
    marginLeft: 100,
    backgroundColor: 'orange',
    padding: 10,
    borderRadius: 5,
  },
  logoText: {
    color: 'white',
    fontWeight: '600',
    fontSize: 18,
  },
});
