import React, { useState, useEffect } from 'react';
import { View, Text, Image, FlatList, StyleSheet, Dimensions } from 'react-native';
import { initializeApp } from 'firebase/app';
import { getDatabase, ref, get } from 'firebase/database';

// Initialize Firebase with your config
const firebaseConfig = {
  apiKey: "AIzaSyDhsB4mw--bg4Rp8YJ73k8T4awfcL4Z7zw",
  authDomain: "k-mart-1cf75.firebaseapp.com",
  databaseURL: "https://k-mart-1cf75-default-rtdb.asia-southeast1.firebasedatabase.app",
  projectId: "k-mart-1cf75",
  storageBucket: "k-mart-1cf75.appspot.com",
  messagingSenderId: "43837954833",
  appId: "1:43837954833:web:768248a59daa6d05704ab0"
};

const firebaseApp = initializeApp(firebaseConfig);
const db = getDatabase(firebaseApp);

const ProductList = () => {
  const [products, setProducts] = useState([]);
  const screenWidth = Dimensions.get('window').width;

  const numColumns = screenWidth > 600 ? 3 : 1; // 3 columns for larger screens, 1 column for smaller screens

  useEffect(() => {
    // Reference to the "products" node in your database
    const productsRef = ref(db, 'products');

    // Fetch the products
    get(productsRef)
      .then((snapshot) => {
        if (snapshot.exists()) {
          const data = snapshot.val();
          if (data) {
            const productArray = Object.values(data);
            setProducts(productArray);
          }
        } else {
          // No data found
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
  }, []);

  return (
    <View style={styles.container}>
      <FlatList
        data={products}
        keyExtractor={(item) => item.name}
        numColumns={numColumns} // Set the number of columns based on screen width
        renderItem={({ item }) => (
          <View style={styles.productCard}>
            <Image source={{ uri: item.photo }} style={styles.productImage} />
            <Text style={styles.productName}>{item.name}</Text>
            <Text style={styles.productPrice}>INR {item.price}</Text>
          </View>
        )}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    padding: 16,
  },
  productCard: {
    flex: 1,
    padding: 16,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  productImage: {
    width: '100%',
    height: 200,
  },
  productName: {
    fontSize: 18,
    fontWeight: 'bold',
    marginVertical: 10,
  },
  productPrice: {
    fontSize: 16,
  },
});

export default ProductList;
