import React from 'react';
import { View, Text, Button, StyleSheet, Image } from 'react-native';

const Cart = ({ cartItems, removeFromCart }) => {
  return ( 
    <View style={styles.cartContainer}>
      <Text style={styles.cartHeader}>Shopping Cart</Text>
      {cartItems.map((item, index) => (
        <View key={index} style={styles.cartItem}>
          <View style={styles.productInfo}>
            <Image source={{ uri: item.photo }} style={styles.productImage} />
            <View style={styles.itemDetails}>
              <Text style={styles.itemName}>{item.name}</Text>
              <Text style={styles.itemDescription}>{item.description}</Text>
              <Text style={styles.itemPrice}>Price: ${item.price}</Text>
            </View>
          </View>
          <Button title="Remove" onPress={() => removeFromCart(index)} style={styles.removeButton} />
        </View>
      ))}
    </View>
  );
};

const styles = StyleSheet.create({
  cartContainer: {
    padding: 16,
  },
  cartHeader: {
    fontSize: 24,
    fontWeight: 'bold',
    textAlign: 'center',
  },
  cartItem: {
    borderBottomWidth: 1,
    borderColor: '#ccc',
    padding: 8,
  },
  productInfo: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  productImage: {
    width: 100,
    height: 100,
  },
  itemDetails: {
    flex: 1,
    marginLeft: 16,
  },
  itemName: {
    fontSize: 18,
    fontWeight: 'bold',
  },
  itemDescription: {
    fontSize: 16, // Increase font size for the description
    marginVertical: 4, // Add some margin between image and texts row
  },
  itemPrice: {
    fontSize: 16,
    fontWeight: 'bold',
  },
  removeButton: {
    backgroundColor: 'red', // Set background color to red
    marginTop: 8, // Add margin between the button and the item
    width: '100%',
  },
});

export default Cart;
