import React, { useState, useEffect } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Dimensions, Modal } from 'react-native';
import { Ionicons } from '@expo/vector-icons'; // Import Ionicons from Expo

export default function Navbar({ onCategorySelect }) {
  const screenWidth = Dimensions.get('window').width;
  const isMobile = screenWidth <= 600; // Define a breakpoint for mobile devices
  const [isMenuVisible, setMenuVisible] = useState(false);

  // Function to toggle the mobile menu
  const toggleMenu = () => {
    setMenuVisible(!isMenuVisible);
  };

  // Render the hamburger icon for mobile devices
  if (isMobile) {
    return (
      <View style={styles.navbar}>
        <TouchableOpacity onPress={toggleMenu}>
          <Ionicons name="ios-menu" size={30} color="white" />
        </TouchableOpacity>
        <View style={styles.logo}>
          <Text style={styles.logoText}>KMART</Text>
        </View>
        <Modal
          visible={isMenuVisible}
          transparent={true}
          animationType="slide"
        >
          <View style={styles.mobileMenu}>
            <TouchableOpacity onPress={toggleMenu}>
              <Ionicons name="ios-close" size={30} color="white" />
            </TouchableOpacity>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Home')}>Home</Text>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Men')}>Men</Text>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Women')}>Women</Text>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Electronics')}>Electronics</Text>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Kids')}>Kids</Text>
            <Text style={styles.mobileMenuItem} onPress={() => onCategorySelect('Home-Decor')}>Home-Decor</Text>
            
          </View>
        </Modal>
      </View>
    );
  }

  // Render the full version for laptops and wider screens
  return (
    <View style={styles.navbar}>
      <TouchableOpacity onPress={() => onCategorySelect('Home')}>
        <Text style={styles.navItem}>Home</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Men')}>
        <Text style={styles.navItem}>Men</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Women')}>
        <Text style={styles.navItem}>Women</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Electronics')}>
        <Text style={styles.navItem}>Electronics</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Kids')}>
        <Text style={styles.navItem}>Kids</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => onCategorySelect('Home-Decor')}>
        <Text style={styles.navItem}>Home-Decor</Text>
      </TouchableOpacity>
      
      <View style={styles.logo}>
        <Text style={styles.logoText}>KMART</Text>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  navbar: {
    flexDirection: 'row',
    justifyContent: 'space-around',
    alignItems: 'center',
    backgroundColor: 'darkslategray',
    paddingHorizontal: 10,
    height: 60,
    width: '100%',
  },
  
  navItem: {
    color: 'white',
    marginHorizontal: 10,
    fontSize: 18,
  },
  
  logo: {
    paddingRight: 20,
    paddingLeft: 20,
    marginLeft: 100,
    backgroundColor: 'orange',
    padding: 10,
    borderRadius: 5,
  },
  logoText: {
    color: 'white',
    fontWeight: '600',
    fontSize: 18,
  },
  mobileMenu: {
    flex: 1,
    justifyContent: 'flex-start',
    alignItems: 'flex-end',
    backgroundColor: 'darkslategray',
    paddingHorizontal: 10,
    paddingTop: 30,
  },
  mobileMenuItem: {
    color: 'white',
    fontSize: 18,
    marginVertical: 10,
  },
});
